import Handlebars from 'handlebars'
import puppeteer from 'puppeteer'
import generateMaterial from '../generateMaterial/generateMaterial'

async function generatePDF(data: { content: string, model: string }) {

	type BrowserOptions = {
		headless: boolean
		args: string[]
		executablePath?: string
	}

	const iaMaterial = await generateMaterial(data)
	const newData = { content: iaMaterial }
	const templateHtml =
		`<!DOCTYPE html>
		<html>
		<head>
  		<title>PDF Template</title>
  		<link rel="preconnect" href="https://fonts.googleapis.com">
  		<link rel="preconnect" href="https://fonts.gstatic.com" crossorigin>
  		<link href="https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,100..900;1,100..900&display=swap" rel="stylesheet">
  		<style>
    		body { font-family: 'Montserrat', Arial, sans-serif; margin: 0; padding: 0; color: #5E5E5E; }
    		.content { margin-inline: 60px; }
    		h1 { margin-top: 0px; }
    		.page-break { page-break-before: always; }
    		@page {
      		margin: 130px 0;
    		}
    		@media print {
      		.content {
        		page-break-inside: avoid;
      		}
    		}
  		</style>
		</head>
		<body>
  		<div class="content">
    		<div>{{{content}}}</div>
  		</div>
		</body>
		</html>`


	const headerHtml =
		`<!DOCTYPE html>
		<html>
		<head>
  		<style>
    		body { font-family: 'Montserrat', Arial, sans-serif; font-size: 10px; }
    		.template { width: 100%; text-align: center; opacity: 0.8; margin-block: 20px; }
  		</style>
		</head>
		<body>
  		<div class="template">
    		<img alt="Template Image" height="30px" src="data:image/png;base64,">
  		</div>
		</body>
		</html>`

	const footerHtml =
		`<!DOCTYPE html>
		<html>
		<head>
  		<style>
    		body { font-family: 'Montserrat', Arial, sans-serif; font-size: 10px; }
    		.template { width: 100%; text-align: center; opacity: 0.8; margin-block: 20px; }
  		</style>
		</head>
		<body>
  		<div class="template">
    		<img alt="Template Image" height="30px" src="data:image/png;base64,">
  		</div>
		</body>
		</html>`

	const template = Handlebars.compile(templateHtml)
	const htmlContent = template(newData)

	const browserOptions: BrowserOptions = {
		headless: true,
		args: ['--no-sandbox', '--disable-setuid-sandbox'],
	}

	const environment = process.env.ENVIROMENT

	if (environment !== 'development') browserOptions.executablePath = '/usr/bin/chromium-browser'

	const browser = await puppeteer.launch(browserOptions)
	const page = await browser.newPage()

	await page.setContent(htmlContent, { waitUntil: 'networkidle0' })

	const pdfBuffer = await page.pdf({
		format: 'A4',
		printBackground: true,
		displayHeaderFooter: true,
		headerTemplate: headerHtml,
		footerTemplate: footerHtml,
		margin: { top: '100px', bottom: '100px', left: '20px', right: '20px' }
	})

	await browser.close()
	// const pdfPath = path.join(__dirname, 'output.pdf')
	// fs.writeFileSync(pdfPath, pdfBuffer)

	return pdfBuffer
}

export default generatePDF
